import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from "./FreepostingPage.module.css";
import arrow from '../images/arrow.png';  // 뒤로 가기 화살표
import bar from '../images/bar.png';  // 바 이미지
import Header from './_.js';  // 상단바 컴포넌트

const FreepostingPage = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const [content, setContent] = useState('');  // 게시글 내용
  const [comments, setComments] = useState([]);  // 댓글들
  const [commentContent, setCommentContent] = useState('');  // 댓글 내용
  const [isHeartFilled, setIsHeartFilled] = useState(false);  // 좋아요 상태
  const [isPopupOpen, setIsPopupOpen] = useState(false);  // 신고 팝업 상태

  const currentDate = new Date();
  const year = currentDate.getFullYear();
  const month = String(currentDate.getMonth() + 1).padStart(2, '0');
  const day = String(currentDate.getDate()).padStart(2, '0');
  const formattedDate = `${year}.${month}.${day}`;  // 게시글 작성일

  // 뒤로 가기 버튼 클릭 처리
  const handleBackClick = () => navigate(-1);

  // 댓글 내용 변화 처리
  const handleContentChange = (e) => setContent(e.target.value);

  const handleCommentChange = (e) => setCommentContent(e.target.value);

  // 댓글 추가 처리
  const handleAddComment = () => {
    if (commentContent.trim() !== '') {
      const newComment = { nickname: 'User', content: commentContent, replies: [] };
      setComments([...comments, newComment]);
      setCommentContent('');
    }
  };

  // 좋아요 클릭 처리
  const handleHeartClick = () => setIsHeartFilled(!isHeartFilled);

  // 신고 팝업 열기 / 닫기
  const togglePopup = () => setIsPopupOpen(!isPopupOpen);

  // 신고 제출
  const submitReport = async () => {
    console.log("신고 내용:", commentContent);
    togglePopup();
  };

  return (
    <div>
      <Header />
      <div className={styles["content"]}>
        <img src={arrow} className={styles["app-arrow"]} alt="back_arrow" onClick={handleBackClick} />
        <h1 className={styles["title-text2"]}>자유게시판</h1>

        <img src={bar} className={styles["app-bar"]} alt="bar" />

        <h1 className={styles["title-text3"]}>게시판 제목</h1>

        <h2 className={styles["title-text4"]}>작성일: {formattedDate}</h2>

        <button onClick={togglePopup} className={styles["report-button"]}>
          신고하기
        </button>
      </div>

      {isPopupOpen && (
        <div className={styles["popup"]}>
          <div className={styles["popup-inner"]}>
            <h3>신고하기</h3>
            <textarea className={styles["popup-textarea"]} value={commentContent} onChange={handleCommentChange} />
            <div className={styles["popup-button-container"]}>
              <button onClick={togglePopup} className={styles["popup-close"]}>닫기</button>
              <button onClick={submitReport} className={styles["popup-receive"]}>제출</button>
            </div>
          </div>
        </div>
      )}

      <div className={styles["content-input"]}>
        <textarea
          className={styles["textarea"]}
          value={content}
          onChange={handleContentChange}
          placeholder="게시글을 입력하세요."
        />
      </div>

      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        accept="image/*"
      />

      <img
        src={isHeartFilled ? '/path/to/filled-heart-icon' : '/path/to/heart-icon'}
        className={styles["app-heart"]}
        alt="heart"
        onClick={handleHeartClick}
      />

      <div className={styles["content-input2"]}>
        <textarea
          className={styles["textarea2"]}
          value={commentContent}
          onChange={handleCommentChange}
          placeholder="댓글을 입력하세요."
        />
      </div>

      <button onClick={handleAddComment}>댓글 달기</button>

      <div className={styles["comments-section"]}>
        {comments.map((comment, index) => (
          <div key={index} className={styles["comment-item"]}>
            <strong>{comment.nickname}:</strong> {comment.content}
          </div>
        ))}
      </div>
    </div>
  );
};

export default FreepostingPage;
