import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMediaQuery } from 'react-responsive'; // 반응형 페이지 만들기 위함
import Header from './_.js';  // 상단바 컴포넌트
import styles from './QuestionboardPage.module.css';  // BoardPage용 CSS 파일
import CommunicationRoom_goBack from '../images/왼쪽 나가기 버튼.png';
import DownMenu from '../images/아래방향메뉴선택.png';
import SearchIcon from '../images/돋보기아이콘.png';  // 돋보기 아이콘
import IconScrap from '../images/횃불이스크랩.png';
import IconUnscrap from '../images/횃불이스크랩X.png';

const BASE_URL = 'http://<your-domain>/api/board';  // API Base URL

// 인증 헤더 설정
const getAuthHeaders = () => {
  const accessToken = localStorage.getItem('accessToken');  // localStorage에서 accessToken을 가져옴
  return {
    'Authorization': `Bearer ${accessToken}`,
    'Content-Type': 'application/json',
  };
};

const QuestionboardPage = () => {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [scrapStatus, setScrapStatus] = useState({});
  const [posts, setPosts] = useState([]);
  const [sortType, setSortType] = useState('latest'); // 'latest' or 'recommend'

  const isDesktop = useMediaQuery({ query: '(min-width: 769px)' });

  useEffect(() => {
    // 퀘스트 목록 불러오기 (페이지네이션 및 검색 포함)
    const fetchPosts = async () => {
      const params = new URLSearchParams({
        page: 0,  // 기본 페이지 번호
        size: 10,  // 한 페이지 당 항목 수
        searchKeyword: searchTerm,  // 검색어
      });

      try {
        const response = await fetch(`${BASE_URL}/quest?${params.toString()}`, {
          method: 'GET',
          headers: getAuthHeaders(),
        });
        const data = await response.json();
        setPosts(data.content);  // 받아온 퀘스트 목록을 상태에 저장
        setScrapStatus(data.content.reduce((acc, post) => ({ ...acc, [post.id]: post.scrap === 1 }), {})); // 스크랩 상태 초기화
      } catch (error) {
        console.error('퀘스트 목록 불러오기 오류:', error);
      }
    };

    fetchPosts();
  }, [searchTerm]);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleBoardChange = (boardName) => {
    if (boardName === '자유 게시판') {
      navigate('/FreeboardPage');  // 자유 게시판으로 이동
    }
    setMenuOpen(false);
  };

  const toggleScrap = async (id) => {
    try {
      const response = await fetch(`${BASE_URL}/quest/${id}/scrap`, {
        method: 'POST',
        headers: getAuthHeaders(),
      });
      if (response.ok) {
        setScrapStatus((prevState) => ({
          ...prevState,
          [id]: !prevState[id],
        }));
      } else {
        throw new Error('스크랩 상태 업데이트 실패');
      }
    } catch (error) {
      console.error('스크랩 상태 업데이트 중 오류 발생:', error);
    }
  };

  const handleSearchInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearch = () => {
    // 검색어를 기반으로 퀘스트 목록 다시 불러오기
    setSearchTerm(searchTerm.trim());
  };

  const handleSort = (type) => {
    setSortType(type);
    // 서버에서 정렬된 데이터를 불러오거나, 클라이언트에서 정렬할 수 있습니다.
  };

  return (
    <div className={styles.container}>
      <Header />
      <div className={styles.content}>
        <div className={`${styles.titleContainer} ${isDesktop ? styles.desktopTitleContainer : ''}`}>
          <img
            src={CommunicationRoom_goBack}
            className={`${styles.goBackButton} ${isDesktop ? styles.desktopGoBackButton : ''}`}
            alt="뒤로가기"
            onClick={() => navigate(-1)}
          />
          <h1 className={`${styles.pageTitle} ${isDesktop ? styles.desktopPageTitle : ''}`}>
            질문 게시판
          </h1>
          <img
            src={DownMenu}
            className={`${styles.downMenuButton} ${isDesktop ? styles.desktopDownMenuButton : ''}`}
            alt="게시판 선택"
            onClick={toggleMenu}
          />
        </div>

        {menuOpen && (
          <div className={`${styles.dropdownMenu} ${isDesktop ? styles.desktopDropdownMenu : ''}`}>
            <div className={`${styles.menuItem} ${isDesktop ? styles.desktopMenuItem : ''}`} onClick={() => handleBoardChange('자유 게시판')}>
              자유 게시판
            </div>
          </div>
        )}

        <div className={`${styles.controlPanel} ${isDesktop ? styles.desktopControlPanel : ''}`}>
          <button className={`${styles.writeButton} ${isDesktop ? styles.desktopWriteButton : ''}`} onClick={() => navigate('/QuestionwritePage')}>
            글쓰기
          </button>
          <div className={`${styles.searchBar} ${isDesktop ? styles.desktopSearchBar : ''}`}>
            <input
              type="text"
              value={searchTerm}
              onChange={handleSearchInputChange}
              className={`${styles.searchInput} ${isDesktop ? styles.desktopSearchInput : ''}`}
              placeholder="검색어 입력"
            />
            <img
              src={SearchIcon}
              className={`${styles.searchIcon} ${isDesktop ? styles.desktopSearchIcon : ''}`}
              alt="검색"
              onClick={handleSearch}
            />
          </div>
          <div className={`${styles.sortButtons} ${isDesktop ? styles.desktopSortButtons : ''}`}>
            <button className={`${styles.sortButton} ${styles.latestSortButton} ${isDesktop ? styles.desktopLatestSortButton : ''}`} onClick={() => handleSort('latest')}>
              최신순
            </button>
            <button className={`${styles.sortButton} ${styles.recommendSortButton} ${isDesktop ? styles.desktopRecommendSortButton : ''}`} onClick={() => handleSort('recommend')}>
              추천순
            </button>
          </div>
        </div>

        <div className={styles.postList}>
          {posts.map((post) => (
            <div key={post.id} className={styles.postItem}>
              {post.questLike > 10 && <span className={styles.hotTag}>HOT</span>}
              <div className={styles.postInfo}>
                <span className={styles.postTitle} onClick={() => navigate(`/quest/${post.id}`)}>{post.questTitle}</span>
                <span className={styles.postDate}>{new Date(post.questCreatedTime).toLocaleDateString()}</span>
              </div>
              <img
                src={scrapStatus[post.id] ? IconScrap : IconUnscrap}
                alt={scrapStatus[post.id] ? '스크랩됨' : '스크랩안됨'}
                className={styles.scrapIcon}
                onClick={() => toggleScrap(post.id)}
              />
            </div>
          ))}
        </div>

        <div className={styles.pagination}>
          {/* 페이지네이션 처리 */}
        </div>
      </div>
    </div>
  );
};

export default QuestionboardPage;
